void.string:
-- CREATE SYSTEM DATA PACKAGE -- 

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- Create archive from extracted data
WbSysExec -program='python3' -argument='"$[pwb_path]/create_data_package.py"' -env="PATH=$[py_path]";

-- VERIFY CHECKSUM --

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- md5sum check
WbSysExec -program='python3' -argument='"$[pwb_path]/verify_md5sum.py"' -env="PATH=$[py_path]";

-- MAKE COPIES --

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- md5sum check
WbSysExec -program='python3' -argument='"$[pwb_path]/verify_make_copies.py"' -env="PATH=$[py_path]";

-- PROCESS FILES:

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- Fix directories and generate sql if needed
WbSysExec -program='python3' -argument='"$[pwb_path]/process_files_pre.py"' -env="PATH=$[py_path]";
-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- Run generated sql (if sql_proc variable exists in config file)																																									
WbInclude -ifNotEmpty=sql_proc -file=tmp/file_process.sql -displayResult=false -verbose=false -continueOnError=false; 																
-- Fix directories post sql-run
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/process_files_post.py"' -env="PATH=$[py_path]";
-- Virus check and metadata extract															
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/process_files_check.py"' -env="PATH=$[py_path]";	
-- Convert files			
-- WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/process_files_convert.py"' -env="PATH=$[py_path]";	
-- Save log/unmount wim image																			
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/wim_umount.py"' -env="PATH=$[py_path]";	
-- Clean up																	
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/cleanup.py"' -env="PATH=$[py_path]";

-- PROCESS METADATA:

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- Normalize metadata and generate sql	
WbSysExec -program='python3' -argument='"$[pwb_path]/process_metadata_pre.py"' -env="PATH=$[py_path]";
-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- Generate DDL from xml																																					
WbInclude -ifNotEmpty=wim_path -file=tmp/meta_process.sql -displayResult=false -verbose=false -continueOnError=false; 		
-- Generate sql for testing metadata
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/process_metadata_check.py"' -env="PATH=$[py_path]";
-- Generate schemas and import metadata (5 db-engines)															
WbInclude -ifNotEmpty=wim_path -file=tmp/meta_check.sql -displayResult=false -verbose=false -continueOnError=false;		
-- Save log/unmount wim image																			
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/wim_umount.py"' -env="PATH=$[py_path]";	
-- Clean up																	
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/cleanup.py"' -env="PATH=$[py_path]";
